import"./footer-DaZsVk3c.js";/* empty css              *//* empty css                 */function s(){confirm("Estas seguro de cerrar sesion?")&&(alert("Sesion Cerrada correctamente."),localStorage.removeItem("sesionIniciada"),window.location.href="/index.html")}document.getElementById("btn-cerrar").addEventListener("click",function(e){e.preventDefault(),s()});document.getElementById("cambiar-foto").addEventListener("click",function(){document.getElementById("file-input").click()});document.getElementById("file-input").addEventListener("change",function(e){const t=e.target.files[0],n=JSON.parse(localStorage.getItem("usuarios"));if(t){const o=new FileReader;o.onload=function(r){document.getElementById("imagen-usuario").src=r.target.result;const a=document.getElementById("nombre-usuario").value.trim();let i=n.find(c=>c.datosUsuario.nombre===a);i?(i.imagen=r.target.result,localStorage.setItem("usuarios",JSON.stringify(n))):console.log("Usuario no encontrado")},o.readAsDataURL(t)}});
